import React, { Component } from 'react';
import {firebaseApp} from "./firebase";
import {RegisterForm} from "./signUp";
import { Button} from 'react-bootstrap';
import './App.css';
import { withRouter } from 'react-router-dom';
import Bar from './bar';
import {user,db,entry} from './functions';
class ProfilePage extends React.Component {
	constructor(props){
    super(props);
    this.state = {
        inputField: '',
        data: '',
    };
}
onLoad = (e) => {

   	entry().get().then((doc) => {
        if (doc.exists) {
            let data = doc.data()['certificate'];
            this.setState({ data: data });
            console.log("Document data:", data);
        } else {
            // doc.data() will be undefined in this case
            this.setState({ data: null });
            console.log("No such document!");
        }
    })
}
offLoad = (e) => {
	this.setState({data: null});
}

	render() {

		console.log("This is the data", this.state.data);
		let dataUI = this.state.data;
		console.log(dataUI);

		if(dataUI !== undefined){
			console.log("Inside", dataUI);
			let major = dataUI[0];
			let fname = dataUI[1];
			let lname = dataUI[3];
			let units = dataUI[4];
			return (
				<div>
					<Bar />
					  <div>
					<h1>Profile</h1>
					<button onClick={this.offLoad}>Close Certificate</button>
					<div>

					  {

							Object.keys(dataUI).map((key, index) => ( 

						  <p key={index}> this is my key {key} and this {index} is my value {dataUI[key]}</p> 
						))
				  }
				</div>
						</div>		

				</div>
				)	
		}
	else{
    	return (
	<div>
		<Bar />
		  	<div>
				<h1>Profile</h1>
				<button onClick={this.onLoad}>View Certificate</button>
            </div>			
	</div>
	)	
	}}
}

export default withRouter(ProfilePage);
