import React, { Component } from 'react';
import logo from './logo.png';
import './App.css';

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {name: '',sigid: '', major: '', units: ''};

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }  
  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} />
          <p>
            Issue and Verify digital certificates with Ethereum Smart Contracts
          </p>
    <form>
            <label>
                Name:
                <input id="name" type="text" name="name" />
            </label>
        <br />
            Signature ID:
            <input id="sigid"  type="text" name="sigid" />
        <br />
            Major:
            <input id="major" type="text" name="major" />
        <br />
            Units Completed:
            <input id="units" type="text" name="units" />
        <br />
        <form ref="form" onSubmit={this.handleChange}>
        <button type="submit">Verify</button>
        </form>
            <input type="submit" value="Verify" />
    </form>
        </header>
      </div>
    );
  }
  handleChange(event) {
    console.log("doing stuff");
    this.setState({name: event.target.name,sigid: event.target.sigid, major: event.target.major, units: event.target.units});
  }

  handleSubmit(event) {
    alert('A name was submitted: ' + this.state.name);
    console.log(this.state.name);
    event.preventDefault();
  }
}

export default App;
