import React, { Component } from 'react';
import logo from './logo.png';
import './App.css';
import {firebaseApp} from "./firebase";
function user() {
    return firebaseApp.auth().currentUser;
}
function db() {
    return firebaseApp.firestore().collection('users');
}
function entry() {
    return db().doc(user().email);
}
class App extends Component {
    constructor(props) {
        super(props);
	this.count = 0;
        this.state = {
            name: '',
            sigid: '', 
            major: '', 
            units: '',
	showComponent:false,
        };
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }  
 	SignUp(){
        console.log('In Sign UP Method: The state:', this.state);
        const {name,email, password} = this.state;
        firebaseApp.auth().createUserWithEmailAndPassword(email,password)
            .catch(error => {
                this.setState({error})
            });
        console.log("This is the name and email in SignUP" + name + " " + email + " "); 
	}
	render() {
    	return (<form onSubmit={this.handleSignIn.bind(this)}>
        <h3>Sign in</h3>
        <input type="text" ref="username" placeholder="enter you username" />
        <input type="password" ref="password" placeholder="enter password" />
        <input type="submit" value="Login" />
      </form>
    );
  }

  handleChange(event) {
    this.setState({
        [event.target.name]:event.target.value
	});
  }

  handleSubmit(event) {
	this.count += 1;	
	console.log("this is count" + this.count);
    alert('A name was submitted: ' + this.state.name);
    console.log(this.state.name + "where is this");
    event.preventDefault();
  }
}

export default App;
