import React, { Component } from 'react';
import {firebaseApp} from "./firebase";
import {RegisterForm} from "./signUp";
import { Button} from 'react-bootstrap';
import './App.css';
import { withRouter } from 'react-router-dom';
import Bar from './bar';
import {user,db,entry} from './functions';
class ProfilePage extends React.Component {
	constructor(props){
    super(props);
    this.state = {
        inputField: '',
        data: [],
    };
}
onLoad = (e) => {

   	entry().get().then((doc) => {
        if (doc.exists) {
            let data = doc.data()['certificate'];
            this.setState({ data});
            console.log("Document data:", data);
        } else {
            // doc.data() will be undefined in this case
            this.setState({ data: null });
            console.log("No such document!");
        }
    });
}

	render() {
		const { inputField, data } = this.state;
		console.log("This is the data", this.state.data);
		console.log(data);
    	return (
	<div>
		<Bar />
		  <div>
		<h1>Profile</h1>
                <span>{<div>{[0].name}</div>}</span>
            </div>		
		<button onClick={this.onLoad}>View Certificate</button>
	</div>
	)	
	}
}

export default withRouter(ProfilePage);
