import React, { Component } from 'react';
import logo from './logo.png';
import './App.css';

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            name: '',
            sigid: '', 
            major: '', 
            units: ''
        };
	this.majorData = [
    	{ value: 'USA', name: 'USA' },
    	{ value: 'CANADA', name: 'CANADA' }            
];
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }  
  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} alt="Logo" />
          <p>
            Issue and Verify digital certificates with Ethereum Smart Contracts
          </p>
    <form onSubmit={this.handleSubmit}>
            <label>
                Name:
                <input id="name" type="text" name="name" onChange={this.handleChange}/>
            </label>
        <br />
            Signature ID:
            <input id="sigid"  type="text" name="sigid" onChange={this.handleChange}/>
        <br />
            Major:
            <input id="major" type="text" name="major" onChange={this.handleChange}/>
        <br />
            Units Completed:
            <input id="units" type="text" name="units" onChange={this.handleChange}/>
        <br />
            <input type="submit" value="Submit" />
    </form>
        </header>
         <select 
         defaultValue={this.state.selectValue} 
         onChange={this.handleChange} 
         >
            <option value="Orange">Orange</option>
            <option value="Radish">Radish</option>
            <option value="Cherry">Cherry</option>
          </select>
          <p>{message}</p>
      </div>
    );
  }
  handleChange(event) {
    console.log("doing stuff");
    this.setState({
        [event.target.name]:event.target.value
    });
  }

  handleSubmit(event) {
    alert('A name was submitted: ' + this.state.name);
    console.log(this.state.name + "where is this");
    event.preventDefault();
  }
}

export default App;
